var blpapi = require('blpapi');
var config = { serverHost: '127.0.0.1', serverPort: 8199 }; // INCORRECT(!!!) IP
var session;
var i = 1;
function newSession() {
    session = new blpapi.Session(config);
    session.on('SessionStartupFailure', function(m) {
        // Kill session that failed
        session.stop();
        session.destroy();
        // Start over again on the next tick (to avoid recursion)
        process.nextTick(newSession);
    });
    console.log('Starting...', i++);
    session.start();
}
newSession();
